circuits.Z:
OPENQASM 2.0;
include "qelib1.inc";
qreg q[4];
creg c[4];

// Hartree-Fock state |0011>
x q[0];
x q[1];

// Layer 1: Ry rotations
ry(-0.000004) q[0];
ry(3.141631) q[1];
ry(0.000024) q[2];
ry(0.182704) q[3];

// Entangling layer
cx q[0], q[1];
cx q[1], q[2];
cx q[2], q[3];

// Layer 2: Ry rotations
ry(0.000023) q[0];
ry(-0.000060) q[1];
ry(0.000014) q[2];
ry(0.182708) q[3];

// Measure all qubits
measure q[0] -> c[0];
measure q[1] -> c[1];
measure q[2] -> c[2];
measure q[3] -> c[3];

