circuits.cycle_a_p1:
version 3.0
qubit[9] q
bit[9] b

// QAOA MaxCut p=1: 4-cycle q4-q6-q8-q7
// gamma = pi/4 = 0.785398, beta = pi/8 = 0.392699
// Edges: (4,6), (6,8), (7,8), (4,7)
// Classical max cut = 4
// ZZ decomposition: CNOT-Rz(-gamma)-CNOT = exp(+i*gamma/2 * ZZ)

// 1. Initial superposition
H q[4]
H q[6]
H q[7]
H q[8]

// 2. Cost unitary: exp(-i*gamma*C) where C = sum (1-ZZ)/2
// Edge (4,6): CNOT-Rz(-gamma)-CNOT
CNOT q[4], q[6]
Rz(-0.785398) q[6]
CNOT q[4], q[6]

// Edge (6,8): CNOT-Rz(-gamma)-CNOT
CNOT q[6], q[8]
Rz(-0.785398) q[8]
CNOT q[6], q[8]

// Edge (7,8): CNOT-Rz(-gamma)-CNOT
CNOT q[7], q[8]
Rz(-0.785398) q[8]
CNOT q[7], q[8]

// Edge (4,7): CNOT-Rz(-gamma)-CNOT
CNOT q[4], q[7]
Rz(-0.785398) q[7]
CNOT q[4], q[7]

// 3. Mixer unitary: Rx(2*beta) on each qubit
Rx(0.785398) q[4]
Rx(0.785398) q[6]
Rx(0.785398) q[7]
Rx(0.785398) q[8]

// 4. Measure
b = measure q

